import requestsimport timefrom lxml import htmlfrom spider_database import insert_data,get_dataimport pymysqlurl = 'http://cba.sports.sina.com.cn/cba/stats/playerstats/'headers = {        'user-agent': 'Mozilla/5.0 (Linux; Android 6.0; Nexus 5 Build/MRA58N) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/73.0.3683.86 Mobile Safari/537.36'}r = requests.get(url,headers=headers)r.encoding = 'gbk'#content="text/html; charset=gbk"# r.encoding("utf-8"]res = r.texts = html.etree.HTML(res)def get_cba_data():    list = []    for i in range(1,101):        res_name = s.xpath(f'//*[@id="table01"]/tbody/tr[{i}]/td[2]/a/text()')        res_team = s.xpath(f'//*[@id="table01"]/tbody/tr[{i}]/td[3]/text()')        res_score = s.xpath(f'//*[@id="table01"]/tbody/tr[{i}]/td[5]/text()')        res_name = ''.join(res_name).strip()        res_team = ''.join(res_team).strip()        res_score = ''.join(res_score).strip()        list.append((res_name, res_team, res_score))    return list    # insert_data()# print(get_data())