import jsonimport requests,refrom lxml import html# url = 'https://www.qinbing.cn/Portal/Index/news/page/2/type/357'## r = requests.get(url)# r.encoding = 'utf-8'# s = html.etree.HTML(r.text)## res = s.xpath('//*[@class="col-lg-8 col-md-8 col-sm-12 col-xs-12 "]/div[1]/div[5]/dl/dd/a/@href')# res_url = ''.join(res)## url_new = 'https://www.qinbing.cn' + res_urldef get_egg_price():    url_new = 'https://www.qinbing.cn/Portal/Index/detial/id/80703/type/357'    r1 = requests.get(url_new)    r1.encoding = 'utf-8'    s1 = html.etree.HTML(r1.text)    res1 = s1.xpath('//*[@class="news"]/div[3]/table/tbody/tr[8]/td[1]/text()')    res2 = s1.xpath('//*[@class="news"]/div[3]/table/tbody/tr[8]/td[2]/text()')    res2 = ''.join(res2).strip()    res3 = s1.xpath('//*[@class="col-lg-8 col-md-12 col-sm-12"]/div/h2/text()')    res3 = ''.join(res3)    city = res1[0]    res1 = ''.join(city).strip()    pattern = '(\d+)年(\d+)月(\d+)日陕西鸡蛋价格行情'    date = (re.search(pattern,res3))    return (date.group(0),res1,res2)