import pymysqldef insert_data_regist(name,passwd,phone):    sql = "insert into regist(username,password,phonenumber) values( '%s','%s','%s')" % (name,passwd,phone)    try:        conn = pymysql.connect(host='127.0.0.1', port=3306, user='root', password='123456', db='cba', charset='utf8')        cursor = conn.cursor()        cursor.execute(sql)        conn.commit()        cursor.close()        conn.close()    except:        conn.rollback()        print('数据存入失败')def get_user_login():    sql = "select username from regist"    conn = pymysql.connect(host='127.0.0.1', port=3306, user='root', password='123456', db='cba', charset='utf8')    cursor = conn.cursor()    cursor.execute(sql)    res_user = cursor.fetchall()    # return res_user    # conn.commit()    cursor.close()    conn.close()    # c = get_user_login()    strs = str([i for i in res_user])    str_new = strs.replace('[', '').replace(']', '').replace('(', '').replace(')', '').replace(',', '').replace("'", '')    return str_newdef get_passwd_login(a):    sql = "select password from regist where username='%s'" % a    conn = pymysql.connect(host='127.0.0.1', port=3306, user='root', password='123456', db='cba', charset='utf8')    cursor = conn.cursor()    cursor.execute(sql)    res_passwd = cursor.fetchall()    # return (res_passwd)    # conn.commit()    cursor.close()    conn.close()    strs = str([i for i in res_passwd])    str_new_passwd = strs.replace('[', '').replace(']', '').replace('(', '').replace(')', '').replace(',', '').replace("'", '')    return str_new_passwd