import timeimport pymysqlfrom datetime import timedeltadef get_time():    time_str = time.strftime('%Y{}%m{}%d{} %X').format('年','月','日')    return time_strimport functoolsfrom flask import session, redirect, app, url_for, requestdef is_login(func):    @functools.wraps(func)    def inner(*args,**kwargs):        user = session.get('user')        if not user:            return redirect('/login')        return func(*args,**kwargs)    return inner